/**
 * Calculates the average (mean) of a series of numbers
 */
export function mean(data) {
    let count = 0;
    let mean = 0;
    for (const n of data) {
        count++;
        mean += (n - mean) / count;
    }
    return count == 0
        ? Number.NaN
        : mean;
}
/**
 * Calculates the standard deviation of a series of numbers.
 */
export function standardDeviation(data) {
    const m = mean(data);
    if (Number.isNaN(m)) {
        return Number.NaN;
    }
    let count = 0;
    let squaredDiff = 0;
    for (const n of data) {
        count++;
        squaredDiff += Math.pow((n - m), 2);
    }
    return Math.sqrt(squaredDiff / count);
}
