/**
 * A change message carrying information about the change type, id and previous and new value.
 */
export interface ElementChange<ELEMENTTYPE, IDTYPE> {
    readonly type: 'CREATE' | 'UPDATE' | 'DELETE';
    readonly id: IDTYPE;
    readonly previousValue?: ELEMENTTYPE;
    readonly newValue?: ELEMENTTYPE;
}
/**
 * Special case for CREATE and UPDATE messages, when we know that we have a "newValue", saving a null-guard in the
 * resulting code.
 */
export interface ElementChangeCreateUpdate<ELEMENTTYPE, IDTYPE> extends ElementChange<ELEMENTTYPE, IDTYPE> {
    readonly type: 'CREATE' | 'UPDATE';
    readonly id: IDTYPE;
    readonly previousValue?: ELEMENTTYPE;
    readonly newValue: ELEMENTTYPE;
}
